function Valve({ valveId, status, toggleValve }) {
    const color = status === 'open' ? 'green' : 'red';

    return (
      <div 
        onClick={() => toggleValve(valveId)} 
        style={{ color, cursor: 'pointer' }}
        title={`${valveId} - ${status}`}
      >
        <img src={`-${status}.png`} alt={`${valveId} - ${status}`} />
      </div>
    );
  }

export default Valve
